CREATE TABLE  ALLERGY
(
ID INT AUTO_INCREMENT PRIMARY KEY,
NAME VARCHAR(100) NOT NULL UNIQUE
);

CREATE TABLE  MEDICATION
(
ID INT AUTO_INCREMENT PRIMARY KEY,
NAME VARCHAR(100) NOT NULL UNIQUE
);

CREATE TABLE  MEDICAL_RECORD
(
ID INT AUTO_INCREMENT PRIMARY KEY,
PERSON_ID INT,
FOREIGN KEY (PERSON_ID) REFERENCES PERSON(ID)
);

